    constructor(props) {
        super(props)
        this.state = {
            name: ''
        }
    }
    handleNameChange = event => {
        event.preventDefault()
        this.setState({
            name: event.target.value
        })
    }
    handleSubmit = event => {
        event.preventDefault()
        const data = this.state
        console.log(data)
