    render() {
        const { name, data } = this.state
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Name:
                 <input type="text" ref={el => this.element = el} />
                </label>
                <input type="submit" value="Submit" />
                <p>You entered {name}</p>      
            </form>      
        );
    }
}
