class Form extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            topic: '',
            subject: ''
        };
    }

    addPost = e => {
        e.preventDefault();
        const db = firebase.firestore();
        db.settings({
            timestampsInSnapshots: true
        });
        const userRef = db.collection('posts').add({
            topic: this.state.topic,
            subject: this.state.subject
        });
        this.setState({
            topic: '',
            subject: ''
        });
    };

    handleSubmit = event => {
        event.preventDefault()
        this.setState({ [event.target.name]: event.target.value });
    }

    render() {
        const { topic, subject } = this.state
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Topic:
                 <input type="text" name="topic" value="topic" ref={el => this.element = el} />
                </label>
                <label>
                    Subject:
                 <input type="text" name="subject" value="subject" ref={el => this.element = el} />
                </label>
                <input type="submit" value="Submit" />
                <p>You entered: {topic}</p>
                <p>Subject: {subject}</p>
            </form>
        );
    }
}
export default Form;
