    constructor(props) {
        super(props)
        this.state = {
            topic: '',
            subject: ''
        };
    }

    addPost = e => {
        e.preventDefault();
        const db = firebase.firestore();
        db.settings({
            timestampsInSnapshots: true
        });
        const userRef = db.collection('posts').add({
            topic: this.state.topic,
            subject: this.state.subject
        });
        this.setState({
            topic: '',
            subject: ''
        });
    };

    handleSubmit = event => {
        event.preventDefault()
        this.setState({ [event.target.name]: this.element.value });
        this.setState({ [event.target.name]: this.element.value });
