import React from 'react';
import firebase from 'firebase'
class Post extends React.Component {
    state = { posts: [] };

    componentDidMount() {
        firebase
          .firestore()
          .collection("posts")
          .get()
          .then(querySnapshot => {
            const posts = [];
    
            querySnapshot.forEach(function(doc) {
              posts.push({
                topic: doc.data().team1,
                subject: doc.data().team2,
              });
            });
    
            this.setState({ posts });
          })
          .catch(function(error) {
            console.log("Error getting documents: ", error);
          });
      }

      render() {
        return (
          <div id="against">
            {this.state.posts.map(v => {
              return (
                <p>
                  topic: {v.topic},
                  subject: {v.subject},
                </p>
              );
            })}
          </div>
        );
      }
}
export default Post;
